mapper.select:
select
  h.id as id,username,telephone,amout,times,h.cardId as cardId,c.cardId as acardId,cutdate,status
from
  hairdcard h left join cutlog c on h.cardId = c.cardId,(select @rowno:=0) t
where
  1 = 1

      AND h.cardId = #{cardId}


      AND username = #{username}


      and telephone = #{telephone}


      and status = #{status}

  and signdate between #{startTime} and #{endTime}
  ORDER BY h.signdate DESC
  limit #{size} offset #{offset}

SELECT count(*) from hairdcard
WHERE 1 = 1

    AND cardId = #{cardId}


    AND username = #{username}


    and telephone = #{telephone}


    and status = #{status}

and signdate between #{startTime} and #{endTime}
